everysauce={}

def detect_sauce(inw: str):
    return re.findall("\\d{4,6}",inw)

def get_tags(url):
    html_file =requests.get(url).content
    soup = BeautifulSoup(html_file, 'html.parser')
    return {"title":soup.find("meta", attrs={"property" : "og:title"})['content'],"tags":soup.find("meta", attrs={"name" : "twitter:description"})['content'].split(", ")}

